"use client"

import { useAuth } from "@better-auth-ui/react"
import { ArrowLeftRight, LogOut, PlusCircle } from "lucide-react"

import { Button } from "@/components/ui/button"
import {
  Card,
  CardContent,
  CardFooter,
  CardHeader,
  CardTitle
} from "@/components/ui/card"
import { Spinner } from "@/components/ui/spinner"
import { UserView } from "@/components/user/user-view"
import { useSession } from "@/hooks/auth/use-session"
import { useListDeviceSessions } from "@/hooks/settings/use-list-device-sessions"
import { useRevokeMultiSession } from "@/hooks/settings/use-revoke-multi-session"
import { useSetActiveSession } from "@/hooks/settings/use-set-active-session"
import { cn } from "@/lib/utils"

export type AccountsProps = {
  className?: string
}

/**
 * Display and manage multiple signed-in accounts for multi-session support.
 *
 * Shows all device sessions, allows switching between accounts, and signing out
 * of individual accounts. Shown in AccountSettings when multiSession is enabled.
 *
 * @returns A JSX element containing the accounts card, or null if multiSession is disabled
 */
export function Accounts({ className }: AccountsProps) {
  const { basePaths, localization, viewPaths, Link } = useAuth()
  const { data: sessionData } = useSession()
  const { data: deviceSessions, isPending } = useListDeviceSessions()
  const { settingActiveSession, setActiveSession } = useSetActiveSession()
  const { revokingSession, revokeSession } = useRevokeMultiSession()

  return (
    <Card className={cn("w-full py-4 md:py-6 gap-4 md:gap-6", className)}>
      <CardHeader className="px-4 md:px-6 gap-0">
        <CardTitle className="text-xl">
          {localization.settings.accounts}
        </CardTitle>
      </CardHeader>

      <CardContent className="px-4 md:px-6 grid gap-3">
        {sessionData && !isPending ? (
          [
            sessionData,
            ...(deviceSessions || []).filter(
              (deviceSession) =>
                deviceSession.session.id !== sessionData.session.id
            )
          ].map((deviceSession) => {
            const isActive = deviceSession.session.id === sessionData.session.id
            const isSwitching =
              settingActiveSession === deviceSession.session.token
            const isRevoking = revokingSession === deviceSession.session.token

            return (
              <div
                key={deviceSession.session.id}
                className="h-15 flex items-center rounded-xl border p-3 justify-between"
              >
                <UserView user={deviceSession.user} />

                <div className="flex items-center gap-1 shrink-0">
                  {!isActive && (
                    <Button
                      variant="ghost"
                      size="icon-sm"
                      onClick={() =>
                        setActiveSession(deviceSession.session.token)
                      }
                      disabled={isSwitching || isRevoking}
                      aria-label={localization.auth.switchAccount}
                    >
                      {isSwitching ? <Spinner /> : <ArrowLeftRight />}
                    </Button>
                  )}

                  <Button
                    variant="ghost"
                    size="icon-sm"
                    onClick={() => revokeSession(deviceSession.session.token)}
                    disabled={isSwitching || isRevoking}
                    aria-label={localization.auth.signOut}
                  >
                    {isRevoking ? <Spinner /> : <LogOut />}
                  </Button>
                </div>
              </div>
            )
          })
        ) : (
          <div className="h-15 flex items-center rounded-xl border p-3">
            <UserView isPending />
          </div>
        )}
      </CardContent>

      <CardFooter className="px-4 md:px-6">
        <Button variant="secondary" asChild disabled={isPending}>
          <Link
            href={`${basePaths.auth}/${viewPaths.auth.signIn}`}
            className={cn(isPending && "opacity-50 pointer-events-none")}
          >
            <PlusCircle />

            {localization.auth.addAccount}
          </Link>
        </Button>
      </CardFooter>
    </Card>
  )
}

"use client"

import type { AnyAuthConfig } from "@better-auth-ui/react"
import type { User } from "better-auth"

import { Skeleton } from "@/components/ui/skeleton"
import { useSession } from "@/hooks/auth/use-session"
import { cn } from "@/lib/utils"
import { UserAvatar } from "./user-avatar"

export type UserViewProps = AnyAuthConfig & {
  className?: string
  isPending?: boolean
  user?: User & { username?: string | null; displayUsername?: string | null }
}

/**
 * Render a user view that shows the user's avatar alongside their name and email.
 *
 * @param className - Additional CSS classes applied to the outer container
 * @param isPending - When true, force rendering of the loading skeleton (unless an explicit `user` prop is provided)
 * @param user - Optional user object to display; when omitted the current session user is used if available
 * @returns A React element that displays the user's avatar with their name and email
 */
export function UserView({ className, isPending, user }: UserViewProps) {
  const { data: sessionData, isPending: sessionPending } = useSession()

  const resolvedUser = user ?? sessionData?.user

  if ((isPending || sessionPending) && !user) {
    return (
      <div className={cn("flex items-center gap-2", className)}>
        <UserAvatar isPending />

        <div className="grid flex-1 gap-1 text-left text-sm">
          <Skeleton className="h-4 w-24" />
          <Skeleton className="h-3 w-32" />
        </div>
      </div>
    )
  }

  return (
    <div className={cn("flex items-center gap-2", className)}>
      <UserAvatar user={resolvedUser} />

      <div className="grid flex-1 text-left text-sm leading-tight">
        <span className="truncate font-medium">
          {resolvedUser?.displayUsername ||
            resolvedUser?.name ||
            resolvedUser?.email}
        </span>

        {(resolvedUser?.displayUsername || resolvedUser?.name) && (
          <span className="text-muted-foreground truncate text-xs">
            {resolvedUser?.email}
          </span>
        )}
      </div>
    </div>
  )
}

import { useAuth } from "@better-auth-ui/react"
import { type DefinedInitialDataOptions, useQuery } from "@tanstack/react-query"

/**
 * Fetches and returns the session data.
 *
 * @param options - Optional query options.
 * @returns Query result containing session data, loading state, and error state
 */
export function useSession(options?: Partial<DefinedInitialDataOptions>) {
  const { authClient } = useAuth()

  return useQuery({
    queryKey: ["auth", "session"],
    queryFn: () => authClient.getSession({ fetchOptions: { throw: true } }),
    ...(options as object)
  })
}

import { useAuth, useSession } from "@better-auth-ui/react"
import { type DefinedInitialDataOptions, useQuery } from "@tanstack/react-query"

/**
 * Fetches and returns the list of device sessions for multi-session support.
 *
 * @param options - Optional query options.
 * @returns Query result containing device sessions data, loading state, and error state
 */
export function useListDeviceSessions(options?: DefinedInitialDataOptions) {
  const { authClient, multiSession } = useAuth()
  const { data: sessionData } = useSession(options)

  return useQuery({
    queryKey: ["auth", "multiSession", "listDeviceSessions"],
    queryFn: async () =>
      authClient.multiSession.listDeviceSessions({
        fetchOptions: { throw: true }
      }),
    enabled: multiSession && !!sessionData,
    ...(options as object)
  })
}

import { useAuth } from "@better-auth-ui/react"
import { useQueryClient } from "@tanstack/react-query"
import { useCallback, useState } from "react"

/**
 * Provides functionality to set an active session from device sessions in multi-session mode.
 *
 * @returns An object containing the pending session token and a function to set the active session
 */
export function useSetActiveSession() {
  const queryClient = useQueryClient()
  const { authClient, toast } = useAuth()

  const [settingActiveSession, setSettingActiveSession] = useState<
    string | null
  >(null)

  const setActiveSession = useCallback(
    async (sessionToken: string) => {
      setSettingActiveSession(sessionToken)

      const { error } = await authClient.multiSession.setActive({
        sessionToken
      })

      if (error) {
        toast.error(error.message || error.statusText)
      } else {
        await queryClient.invalidateQueries({ queryKey: ["auth"] })
      }

      setSettingActiveSession(null)
    },
    [authClient, queryClient, toast]
  )

  return { settingActiveSession, setActiveSession }
}

import { useAuth } from "@better-auth-ui/react"
import { useQueryClient } from "@tanstack/react-query"
import { useCallback, useState } from "react"

/**
 * Provides functionality to revoke a session from device sessions in multi-session mode.
 * This is used for the multi-session plugin where users can have multiple accounts on the same device.
 *
 * @returns An object containing the revoking session token and a function to revoke a session
 */
export function useRevokeMultiSession() {
  const queryClient = useQueryClient()
  const { authClient, toast } = useAuth()

  const [revokingSession, setRevokingSession] = useState<string | null>(null)

  const revokeSession = useCallback(
    async (sessionToken: string) => {
      setRevokingSession(sessionToken)

      const { error } = await authClient.multiSession.revoke({
        sessionToken
      })

      if (error) {
        toast.error(error.message || error.statusText)
      } else {
        await queryClient.invalidateQueries({ queryKey: ["auth"] })
      }

      setRevokingSession(null)
    },
    [authClient, queryClient, toast]
  )

  return { revokingSession, revokeSession }
}
