"use client"

import { type AnyAuthConfig, useChangeEmail } from "@better-auth-ui/react"
import { Check } from "lucide-react"
import { useState } from "react"

import { Button } from "@/components/ui/button"
import {
  Card,
  CardContent,
  CardFooter,
  CardHeader,
  CardTitle
} from "@/components/ui/card"
import { Field, FieldError, FieldLabel } from "@/components/ui/field"
import { Input } from "@/components/ui/input"
import { Skeleton } from "@/components/ui/skeleton"
import { Spinner } from "@/components/ui/spinner"
import { useAuth } from "@/hooks/auth/use-auth"
import { cn } from "@/lib/utils"

export type ChangeEmailProps = AnyAuthConfig & {
  className?: string
}

/**
 * Display and edit the authenticated user's email address in a card-based form.
 *
 * Renders a loading skeleton while the session is pending. When loaded, shows
 * the current email address and a form to update it. A verification email will
 * be sent to the new address upon submission.
 *
 * @returns A JSX element containing the change email card and form
 */
export function ChangeEmail({ className, ...config }: ChangeEmailProps) {
  const context = useAuth(config)
  const { authClient, localization } = context

  const { data: sessionData } = authClient.useSession()
  const [state, formAction, isPending] = useChangeEmail(context)

  const [fieldErrors, setFieldErrors] = useState<{
    email?: string
  }>({})

  return (
    <form action={formAction}>
      <Card className={cn("w-full py-4 md:py-6 gap-4 md:gap-6", className)}>
        <CardHeader className="px-4 md:px-6 gap-0">
          <CardTitle className="text-xl">
            {localization.settings.changeEmail}
          </CardTitle>
        </CardHeader>

        <CardContent className="px-4 md:px-6 grid gap-4 md:gap-6">
          <Field className="gap-1">
            <FieldLabel htmlFor="email">{localization.auth.email}</FieldLabel>

            {sessionData ? (
              <Input
                key={sessionData?.user?.email}
                id="email"
                name="email"
                type="email"
                autoComplete="email"
                defaultValue={sessionData?.user?.email || state.email}
                placeholder={localization.auth.emailPlaceholder}
                disabled={isPending}
                required
                onChange={() => {
                  setFieldErrors((prev) => ({
                    ...prev,
                    email: undefined
                  }))
                }}
                onInvalid={(e) => {
                  e.preventDefault()
                  setFieldErrors((prev) => ({
                    ...prev,
                    email: (e.target as HTMLInputElement).validationMessage
                  }))
                }}
                aria-invalid={!!fieldErrors.email}
              />
            ) : (
              <Skeleton className="h-9 w-full" />
            )}

            <FieldError>{fieldErrors.email}</FieldError>
          </Field>
        </CardContent>

        <CardFooter className="px-4 md:px-6">
          <Button type="submit" disabled={isPending || !sessionData}>
            {isPending ? <Spinner /> : <Check />}

            {localization.settings.updateEmail}
          </Button>
        </CardFooter>
      </Card>
    </form>
  )
}

import {
  type AnyAuthConfig,
  type AuthConfig,
  AuthContext
} from "@better-auth-ui/react"
import { deepmerge, defaultConfig } from "@better-auth-ui/react/core"
import { useContext } from "react"
import { toast } from "sonner"

const extendConfig: AnyAuthConfig = {
  toast,
  Link: (props) => <a {...props} />
}

/**
 * Constructs the effective AuthConfig by merging defaults, any AuthContext-provided config, and the optional `config` overrides.
 *
 * @param config - Partial configuration overrides applied on top of context and defaults
 * @returns The final `AuthConfig` with a non-optional `authClient`
 * @throws If the resulting config does not include an `authClient`
 */
export function useAuth(config?: AnyAuthConfig) {
  const context = useContext(AuthContext)

  const authConfig = deepmerge(
    deepmerge(defaultConfig as AnyAuthConfig, extendConfig),
    deepmerge(context || {}, config || {})
  ) as AuthConfig

  if (authConfig.authClient === undefined) {
    throw new Error("[Better Auth UI] authClient is required")
  }

  return authConfig
}

import type { AnyAuthConfig } from "@better-auth-ui/react"
import { useActionState } from "react"

import { useAuth } from "../auth/use-auth"

/**
 * Provides an action state hook that initiates an email change request for the current user.
 *
 * @param config - Optional auth configuration forwarded to the auth hook
 * @returns An action state (initialized with `email` = "") whose action initiates the email change process and displays success or error toasts
 */
export function useChangeEmail(config?: AnyAuthConfig) {
  const { authClient, baseURL, localization, toast, viewPaths } =
    useAuth(config)

  const changeEmail = async (_: object, formData: FormData) => {
    const email = formData.get("email") as string

    const { error } = await authClient.changeEmail(
      {
        newEmail: email,
        callbackURL: `${baseURL}/${viewPaths.settings.account}`
      },
      { disableSignal: true }
    )

    if (error) {
      toast.error(error.message || error.statusText)
    } else {
      toast.success(localization.settings.changeEmailSuccess)
    }

    return { email }
  }

  return useActionState(changeEmail, { email: "" })
}
