"use client"

import type { AnyAuthConfig } from "@better-auth-ui/react"

import { useAuth } from "@/hooks/auth/use-auth"
import { cn } from "@/lib/utils"
import { ChangePassword } from "./change-password"

export type SecuritySettingsProps = AnyAuthConfig & {
  className?: string
}

/**
 * Renders the security settings layout including password management.
 *
 * @param className - Optional additional CSS class names for the outer container.
 * @param config - Auth-related props forwarded to the child components.
 * @returns A JSX element containing a styled container that renders the security settings.
 */
export function SecuritySettings({
  className,
  ...config
}: SecuritySettingsProps) {
  const { emailAndPassword } = useAuth(config)

  return (
    <div className={cn("flex w-full flex-col gap-4 md:gap-6", className)}>
      {emailAndPassword?.enabled && <ChangePassword {...config} />}
    </div>
  )
}

"use client"

import type { AnyAuthConfig } from "@better-auth-ui/react"
import { Check, Eye, EyeOff } from "lucide-react"
import { useState } from "react"

import { Button } from "@/components/ui/button"
import {
  Card,
  CardContent,
  CardFooter,
  CardHeader,
  CardTitle
} from "@/components/ui/card"
import { Field, FieldError, FieldLabel } from "@/components/ui/field"
import { Input } from "@/components/ui/input"
import {
  InputGroup,
  InputGroupAddon,
  InputGroupButton,
  InputGroupInput
} from "@/components/ui/input-group"
import { Spinner } from "@/components/ui/spinner"
import { useAuth } from "@/hooks/auth/use-auth"
import { useSession } from "@/hooks/auth/use-session"
import { useChangePassword } from "@/hooks/settings/use-change-password"
import { cn } from "@/lib/utils"

export type ChangePasswordProps = AnyAuthConfig & {
  className?: string
}

/**
 * Display a form allowing the authenticated user to change their password.
 *
 * Renders a card containing fields for current password, new password, and
 * optionally password confirmation (based on emailAndPassword.confirmPassword config).
 * Upon successful submission, all other sessions are revoked for security.
 *
 * @returns A JSX element containing the change password card and form
 */
export function ChangePassword({ className, ...config }: ChangePasswordProps) {
  const context = useAuth(config)
  const { emailAndPassword, localization } = context

  const { data: sessionData } = useSession(context)

  const [, formAction, isPending] = useChangePassword(context)

  const [isNewPasswordVisible, setIsNewPasswordVisible] = useState(false)
  const [isConfirmPasswordVisible, setIsConfirmPasswordVisible] =
    useState(false)

  const [fieldErrors, setFieldErrors] = useState<{
    currentPassword?: string
    newPassword?: string
    confirmPassword?: string
  }>({})

  return (
    <form action={formAction}>
      <Card className={cn("w-full py-4 md:py-6 gap-4 md:gap-6", className)}>
        <CardHeader className="px-4 md:px-6 gap-0">
          <CardTitle className="text-xl">
            {localization.settings.changePassword}
          </CardTitle>
        </CardHeader>

        <CardContent className="px-4 md:px-6 grid gap-4 md:gap-6">
          <Field className="gap-1">
            <FieldLabel htmlFor="currentPassword">
              {localization.settings.currentPassword}
            </FieldLabel>

            <Input
              id="currentPassword"
              name="currentPassword"
              type="password"
              autoComplete="current-password"
              placeholder={localization.settings.currentPasswordPlaceholder}
              disabled={isPending || !sessionData}
              required
              onChange={() => {
                setFieldErrors((prev) => ({
                  ...prev,
                  currentPassword: undefined
                }))
              }}
              onInvalid={(e) => {
                e.preventDefault()
                setFieldErrors((prev) => ({
                  ...prev,
                  currentPassword: (e.target as HTMLInputElement)
                    .validationMessage
                }))
              }}
              aria-invalid={!!fieldErrors.currentPassword}
            />

            <FieldError>{fieldErrors.currentPassword}</FieldError>
          </Field>

          <Field className="gap-1">
            <FieldLabel htmlFor="newPassword">
              {localization.auth.newPassword}
            </FieldLabel>

            <InputGroup>
              <InputGroupInput
                id="newPassword"
                name="newPassword"
                type={isNewPasswordVisible ? "text" : "password"}
                autoComplete="new-password"
                placeholder={localization.auth.newPasswordPlaceholder}
                minLength={emailAndPassword?.minPasswordLength}
                maxLength={emailAndPassword?.maxPasswordLength}
                disabled={isPending || !sessionData}
                required
                onChange={() => {
                  setFieldErrors((prev) => ({
                    ...prev,
                    newPassword: undefined
                  }))
                }}
                onInvalid={(e) => {
                  e.preventDefault()
                  setFieldErrors((prev) => ({
                    ...prev,
                    newPassword: (e.target as HTMLInputElement)
                      .validationMessage
                  }))
                }}
                aria-invalid={!!fieldErrors.newPassword}
              />

              <InputGroupAddon align="inline-end">
                <InputGroupButton
                  size="icon-xs"
                  aria-label={
                    isNewPasswordVisible
                      ? localization.auth.hidePassword
                      : localization.auth.showPassword
                  }
                  onClick={() => setIsNewPasswordVisible(!isNewPasswordVisible)}
                  disabled={isPending}
                >
                  {isNewPasswordVisible ? <EyeOff /> : <Eye />}
                </InputGroupButton>
              </InputGroupAddon>
            </InputGroup>

            <FieldError>{fieldErrors.newPassword}</FieldError>
          </Field>

          {emailAndPassword?.confirmPassword && (
            <Field className="gap-1">
              <FieldLabel htmlFor="confirmPassword">
                {localization.auth.confirmPassword}
              </FieldLabel>

              <InputGroup>
                <InputGroupInput
                  id="confirmPassword"
                  name="confirmPassword"
                  type={isConfirmPasswordVisible ? "text" : "password"}
                  autoComplete="new-password"
                  placeholder={localization.auth.confirmPasswordPlaceholder}
                  minLength={emailAndPassword?.minPasswordLength}
                  maxLength={emailAndPassword?.maxPasswordLength}
                  disabled={isPending || !sessionData}
                  required
                  onChange={() => {
                    setFieldErrors((prev) => ({
                      ...prev,
                      confirmPassword: undefined
                    }))
                  }}
                  onInvalid={(e) => {
                    e.preventDefault()
                    setFieldErrors((prev) => ({
                      ...prev,
                      confirmPassword: (e.target as HTMLInputElement)
                        .validationMessage
                    }))
                  }}
                  aria-invalid={!!fieldErrors.confirmPassword}
                />

                <InputGroupAddon align="inline-end">
                  <InputGroupButton
                    size="icon-xs"
                    aria-label={
                      isConfirmPasswordVisible
                        ? localization.auth.hidePassword
                        : localization.auth.showPassword
                    }
                    onClick={() =>
                      setIsConfirmPasswordVisible(!isConfirmPasswordVisible)
                    }
                    disabled={isPending}
                  >
                    {isConfirmPasswordVisible ? <EyeOff /> : <Eye />}
                  </InputGroupButton>
                </InputGroupAddon>
              </InputGroup>

              <FieldError>{fieldErrors.confirmPassword}</FieldError>
            </Field>
          )}
        </CardContent>

        <CardFooter className="px-4 md:px-6">
          <Button type="submit" disabled={isPending || !sessionData}>
            {isPending ? <Spinner /> : <Check />}

            {localization.settings.updatePassword}
          </Button>
        </CardFooter>
      </Card>
    </form>
  )
}
