"use client"

import type { AnyAuthConfig } from "@better-auth-ui/react"
import type { User } from "better-auth"

import { Skeleton } from "@/components/ui/skeleton"
import { useAuth } from "@/hooks/auth/use-auth"
import { useSession } from "@/hooks/auth/use-session"
import { cn } from "@/lib/utils"
import { UserAvatar } from "./user-avatar"

export type UserViewProps = AnyAuthConfig & {
  className?: string
  isPending?: boolean
  user?: User & { username?: string | null; displayUsername?: string | null }
}

/**
 * Render a user view that shows the user's avatar alongside their name and email.
 *
 * @param className - Additional CSS classes applied to the outer container
 * @param isPending - When true, force rendering of the loading skeleton (unless an explicit `user` prop is provided)
 * @param user - Optional user object to display; when omitted the current session user is used if available
 * @returns A React element that displays the user's avatar with their name and email
 */
export function UserView({
  className,
  isPending,
  user,
  ...config
}: UserViewProps) {
  const context = useAuth(config)

  const { data: sessionData, isPending: sessionPending } = useSession(context)

  const resolvedUser = user ?? sessionData?.user

  if ((isPending || sessionPending) && !user) {
    return (
      <div className={cn("flex items-center gap-2", className)}>
        <UserAvatar isPending {...config} />

        <div className="grid flex-1 gap-1 text-left text-sm">
          <Skeleton className="h-4 w-24" />
          <Skeleton className="h-3 w-32" />
        </div>
      </div>
    )
  }

  return (
    <div className={cn("flex items-center gap-2", className)}>
      <UserAvatar user={resolvedUser} {...config} />

      <div className="grid flex-1 text-left text-sm leading-tight">
        <span className="truncate font-medium">
          {resolvedUser?.displayUsername ||
            resolvedUser?.name ||
            resolvedUser?.email}
        </span>

        {(resolvedUser?.displayUsername || resolvedUser?.name) && (
          <span className="text-muted-foreground truncate text-xs">
            {resolvedUser?.email}
          </span>
        )}
      </div>
    </div>
  )
}

"use client"

import type { AnyAuthConfig } from "@better-auth-ui/react"
import type { User } from "better-auth"
import { User2 } from "lucide-react"
import type { ReactNode } from "react"

import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar"
import { Skeleton } from "@/components/ui/skeleton"
import { useAuth } from "@/hooks/auth/use-auth"
import { useSession } from "@/hooks/auth/use-session"
import { cn } from "@/lib/utils"

export type UserAvatarProps = AnyAuthConfig & {
  className?: string
  fallback?: ReactNode
  isPending?: boolean
  user?: User & { username?: string | null; displayUsername?: string | null }
}

/**
 * Display a user's avatar using session information or an explicit user prop.
 *
 * Renders a circular avatar that shows the user's image when available, a fallback node if provided, or the user's first two initials; while the session is loading (or when `isPending` is true) and no `user` prop is supplied, renders a skeleton placeholder.
 *
 * @param className - Additional CSS classes applied to the avatar container
 * @param user - Optional user object to display instead of the session user
 * @param isPending - When true, treat the component as loading and show the skeleton if no `user` is provided
 * @param fallback - Node to render inside the avatar fallback area before initials or the default icon
 * @returns The avatar element to render (JSX)
 */
export function UserAvatar({
  className,
  user,
  isPending,
  fallback,
  ...config
}: UserAvatarProps) {
  const context = useAuth(config)

  const { data: sessionData, isPending: sessionPending } = useSession(context)

  if ((isPending || sessionPending) && !user) {
    return <Skeleton className={cn("size-8 rounded-full", className)} />
  }

  const resolvedUser = user ?? sessionData?.user

  const initials = (
    resolvedUser?.username ||
    resolvedUser?.name ||
    resolvedUser?.email
  )
    ?.slice(0, 2)
    .toUpperCase()

  return (
    <Avatar
      className={cn(
        "size-8 bg-muted text-foreground text-sm rounded-full",
        className
      )}
    >
      <AvatarImage
        src={resolvedUser?.image ?? undefined}
        alt={
          resolvedUser?.displayUsername ||
          resolvedUser?.name ||
          resolvedUser?.email
        }
      />

      <AvatarFallback delayMs={resolvedUser?.image ? 600 : undefined}>
        {fallback || initials || <User2 className="size-4" />}
      </AvatarFallback>
    </Avatar>
  )
}

import {
  type AnyAuthConfig,
  type AuthConfig,
  AuthContext
} from "@better-auth-ui/react"
import { deepmerge, defaultConfig } from "@better-auth-ui/react/core"
import { useContext } from "react"
import { toast } from "sonner"

const extendConfig: AnyAuthConfig = {
  toast,
  Link: (props) => <a {...props} />
}

/**
 * Constructs the effective AuthConfig by merging defaults, any AuthContext-provided config, and the optional `config` overrides.
 *
 * @param config - Partial configuration overrides applied on top of context and defaults
 * @returns The final `AuthConfig` with a non-optional `authClient`
 * @throws If the resulting config does not include an `authClient`
 */
export function useAuth(config?: AnyAuthConfig) {
  const context = useContext(AuthContext)

  const authConfig = deepmerge(
    deepmerge(defaultConfig as AnyAuthConfig, extendConfig),
    deepmerge(context || {}, config || {})
  ) as AuthConfig

  if (authConfig.authClient === undefined) {
    throw new Error("[Better Auth UI] authClient is required")
  }

  return authConfig
}

import type { AnyAuthConfig } from "@better-auth-ui/react"
import { type DefinedInitialDataOptions, useQuery } from "@tanstack/react-query"

import { useAuth } from "../auth/use-auth"

/**
 * Fetches and returns the session data.
 *
 * @param config - Optional partial AuthConfig used to customize auth behavior; may include an `authClient` override.
 * @param options - Optional query options.
 * @returns Query result containing session data, loading state, and error state
 */
export function useSession(
  config?: AnyAuthConfig,
  options?: Partial<DefinedInitialDataOptions>
) {
  const { authClient, queryClient } = useAuth(config)

  return useQuery(
    {
      queryKey: ["auth", "session"],
      queryFn: () => authClient.getSession({ fetchOptions: { throw: true } }),
      ...(options as object)
    },
    queryClient
  )
}
